            gap: 20px;
            margin: 20px auto;
            max-width: 1400px;
        }

        .testimonial-container {
            position: relative;
            width: 1308px;
            /* 3 cards of 436px each */
            overflow: hidden;
            padding: 20px 0;
        }

        .testimonial-wrapper {
            display: flex;
            transition: transform 0.5s ease-in-out;
            gap: 10px;
        }

        .testimonial {
            flex: 0 0 436px;
            background: white;
            padding: 20px;
            border-radius: 10px;
            box-shadow: 0px 4px 8px rgba(0, 0, 0, 0.2);
            text-align: center;
        }

