document.addEventListener("DOMContentLoaded", function () {
    const descriptionElement = document.querySelector(".description");
    const toggleButton = document.querySelector(".toggleBtn");

    const fullText = descriptionElement.textContent.trim();
    const words = fullText.split(" ");

    if (words.length > 60) {
      const truncatedText = words.slice(0, 60).join(" ") + "...";
      descriptionElement.textContent = truncatedText;
      let isTruncated = true;

      toggleButton.addEventListener("click", function () {
        if (isTruncated) {
          descriptionElement.textContent = fullText;
          toggleButton.textContent = "Show Less";
        } else {
          descriptionElement.textContent = truncatedText;
          toggleButton.textContent = "Show More";
        }
        isTruncated = !isTruncated;
      });
    } else {
      toggleButton.style.display = "none"; // Hide button if text is short
    }
  });

