document.addEventListener("DOMContentLoaded", function() {
  // Log to check if script is running
  console.log("Enhanced navigation active script running");

  // Get the current path
  const currentPath = window.location.pathname;
  console.log("Current path:", currentPath);

  // Map routes to exact link text
  const routeToText = {
    '/': 'Home',
    '/about': 'About Us',
    '/faqs': 'FAQs'
  };

  // Try different selectors to find navigation links
  const possibleSelectors = [
    'a.nav-link', 
    '.navbar a', 
    'nav a', 
    '.navigation a',
    '.nav a',
    '.dropdown a',
    'header a',
    'a[href="/"]',
    'a[href="/about"]',
    'a[href="/faqs"]',
    // Add more dynamic selectors
    'ul.menu a',
    '.main-menu a',
    '#main-nav a',
    '.site-navigation a'
  ];

  // Get all links using these selectors
  let navLinks = [];

  possibleSelectors.forEach(selector => {
    const links = document.querySelectorAll(selector);
    console.log(`Found ${links.length} links with selector: ${selector}`);
    if (links.length > 0) {
      links.forEach(link => navLinks.push(link));
    }
  });

  // Remove duplicates using a Map to preserve reference to original elements
  const uniqueLinks = Array.from(new Map(navLinks.map(link => [link, link])).values());
  console.log(`Total unique navigation links found: ${uniqueLinks.length}`);

  // Apply styles to all found links
  uniqueLinks.forEach(link => {
    const linkPath = link.getAttribute('href');
    const linkText = link.textContent.trim();

    console.log(`Checking link: "${linkText}" with href "${linkPath}"`);

    // Reset link style
    link.style.color = '#666666';
    link.style.fontWeight = 'normal';
    link.classList.remove('active');

    // Account for both exact matches and home page with just /
    const isPathMatch = linkPath === currentPath || 
                       (currentPath === '/' && linkPath === '') ||
                       (linkPath && currentPath.startsWith(linkPath) && linkPath !== '/');

    // Check if href matches current path
    if (isPathMatch) {
      console.log(`Match found by href: "${linkText}"`);
      link.style.color = '#000000';
      link.style.fontWeight = 'medium';
      link.classList.add('active');
    }
    // Check if text matches route map
    else if (linkText === routeToText[currentPath]) {
      console.log(`Match found by text: "${linkText}"`);
      link.style.color = '#000000';
      link.style.fontWeight = 'medium';
      link.classList.add('active');
    }
  });

  // Add a global style to ensure consistency
  const style = document.createElement('style');
  style.textContent = `
    a.active {
      color: #000000 !important;
      font-weight: 500 !important; /* 500 is the numeric value for medium */
    }
    .nav a:not(.active), .navbar a:not(.active), nav a:not(.active),
    .navigation a:not(.active), header a:not(.active), .dropdown a:not(.active),
    ul.menu a:not(.active), .main-menu a:not(.active), #main-nav a:not(.active),
    .site-navigation a:not(.active) {
      color: #666666 !important;
      font-weight: normal !important;
    }
  `;
  document.head.appendChild(style);
});
