    <template id="activity-card-template">
        <div class="activity-card">
            <div class="relative">
                <img src="/api/placeholder/448/237" alt="Activity Image" class="activity-card-image" id="activity-image" />
                <div class="activity-badge absolute bg-orange-400 px-4 py-1 text-white" id="activity-type"></div>
                <div class="points-badge absolute flex items-center bg-white px-4 py-1 text-gray-800 shadow-md">
                    <span class="mr-1 text-orange-500">●</span>
                    <span id="activity-points"></span> Points
                </div>
            </div>
            <div class="p-4 sm:p-6">
                <h2 class="mb-2 activity-title" id="activity-title"></h2>
                <p class="mb-4 text-gray-600 activity-description" id="activity-description"></p>

