    <script>
        const activityImageCache = new Map();
        let activityApiCallInProgress = false;
        let activityData = null;

        document.addEventListener('DOMContentLoaded', function() {
            fetchActivityData();
        });

        function getInitials(name) {
            return name
                .split(' ')
                .map(word => word[0])
                .join('')
                .slice(0, 2);
        }

        function createAvatarElement(volunteer) {
            if (volunteer.user_image) {
                const img = document.createElement('img');
                img.className = 'w-8 h-8 rounded-full';
                img.src = '/api/placeholder/32/32';
                img.alt = volunteer.full_name || 'Volunteer';
                loadActivityImage(volunteer.user_image, img);
                return img;
            } else {
                const div = document.createElement('div');
                div.className = 'avatar-initials';
                div.textContent = getInitials(volunteer.full_name || 'User');
                return div;
            }
        }

        function fetchActivityData() {
            if (activityApiCallInProgress) return;
            activityApiCallInProgress = true;

            document.getElementById('activity-loading').style.display = 'block';
            document.getElementById('activity-error').classList.add('hidden');

            if (activityData) {
                renderActivityCards(activityData);
                document.getElementById('activity-loading').style.display = 'none';
                activityApiCallInProgress = false;
                return;
            }

            fetch('/api/method/mykartavya.controllers.api.available_commitments')
                .then(response => {
                    if (!response.ok) {
                        throw new Error('Network response was not ok');
                    }
                    return response.json();
                })
                .then(data => {
                    if (data && data.message && Array.isArray(data.message)) {
                        activityData = data.message;
                        renderActivityCards(activityData);
                    } else {
                        throw new Error('Invalid data format');
                    }
                    document.getElementById('activity-loading').style.display = 'none';
                    activityApiCallInProgress = false;
                })
                .catch(error => {
                    console.error('Error fetching activities:', error);
                    document.getElementById('activity-loading').style.display = 'none';
                    document.getElementById('activity-error').classList.remove('hidden');
                    activityApiCallInProgress = false;
                });
        }

        function renderActivityCards(activities) {
            const cardsContainer = document.getElementById('activity-cards-container');
            const cardTemplate = document.getElementById('activity-card-template');
            const carouselContainer = document.querySelector('.activity-carousel-container');
            const prevBtn = document.getElementById('activity-nav-prev');
            const nextBtn = document.getElementById('activity-nav-next');

            cardsContainer.innerHTML = '';

            if (activities.length === 0) {
                cardsContainer.innerHTML = '<p class="text-center text-gray-500 text-lg">No activities available at the moment.</p>';
                prevBtn.style.display = 'none';
                nextBtn.style.display = 'none';
                return;
            }

            // Handle case when there's only one activity
            if (activities.length === 1) {
                prevBtn.style.display = 'none';
                nextBtn.style.display = 'none';

                // Clear existing classes and set up for single card display
                cardsContainer.className = 'single-card-container';

                const card = cardTemplate.content.cloneNode(true);
                const cardElement = card.querySelector('.activity-card');
                cardElement.className = 'single-card'; // Apply single card styling

                renderCardContent(card, activities[0]);
                cardsContainer.appendChild(card);
                return;
            }

            // Reset for multiple cards
            prevBtn.style.display = 'flex';
            nextBtn.style.display = 'flex';
            cardsContainer.className = 'activity-card-container';

            activities.forEach(activity => {
                const card = cardTemplate.content.cloneNode(true);
                renderCardContent(card, activity);
                cardsContainer.appendChild(card);
            });

            initializeActivityCarousel();
        }

        function renderCardContent(card, activity) {
            let volunteers = [];
            try {
                volunteers = JSON.parse(activity.volunteers || '[]');
            } catch (e) {
                console.error('Error parsing volunteers:', e);
            }

            const activityImage = card.querySelector('#activity-image');
            if (activity.activity_image) {
                loadActivityImage(activity.activity_image, activityImage);
            }

            card.querySelector('#activity-type').textContent = activity.activity_type || 'Activity';
            card.querySelector('#activity-points').textContent = activity.karma_points || 0;
            card.querySelector('#activity-title').textContent = activity.title || 'Untitled Activity';

            const tempDiv = document.createElement('div');
            tempDiv.innerHTML = activity.activity_description || '';
            let description = tempDiv.textContent || tempDiv.innerText || '';
            if (description.length > 100) {
                description = description.substring(0, 100) + '...';
            }
            card.querySelector('#activity-description').textContent = description;

            try {
                const startDate = activity.start_date ? new Date(activity.start_date) : new Date();
                const endDate = activity.end_date ? new Date(activity.end_date) : new Date();

                const formatDate = (date) => {
                    return `${date.getDate()} ${date.toLocaleString('default', { month: 'short' })}, ${date.getFullYear()}`;
                };

                card.querySelector('#activity-date-range').textContent = `${formatDate(startDate)} - ${formatDate(endDate)}`;
            } catch (e) {
                console.error('Error formatting dates:', e);
                card.querySelector('#activity-date-range').textContent = 'Date not available';
            }

            card.querySelector('#activity-hours').textContent = activity.hours || 0;

            const volunteersGroup = card.querySelector('#activity-volunteers-group');
            const moreVolunteers = card.querySelector('#activity-more-volunteers');

            const maxDisplayedVolunteers = 3;
            for (let i = 0; i < Math.min(volunteers.length, maxDisplayedVolunteers); i++) {
                const volunteer = volunteers[i];
                const avatarElement = createAvatarElement(volunteer);
                volunteersGroup.appendChild(avatarElement);
            }

            if (volunteers.length > maxDisplayedVolunteers) {
                moreVolunteers.textContent = `+${volunteers.length - maxDisplayedVolunteers} more`;
            }

            const actNowBtn = card.querySelector('button');
            actNowBtn.addEventListener('click', function() {
                window.location.href = '/frontend/login';
            });
        }

        function initializeActivityCarousel() {
            const container = document.querySelector('.activity-card-container');
            const prevBtn = document.getElementById('activity-nav-prev');
            const nextBtn = document.getElementById('activity-nav-next');
            const cards = document.querySelectorAll('.activity-card');

            // Exit if we don't have a carousel to initialize
            if (!container || container.className === 'single-card-container') {
                return;
            }

            let currentPosition = 0;
            let cardsPerView = getCardsPerView();
            const totalCards = cards.length;

            // Handle edge case: if less cards than view capacity, hide navigation
            if (totalCards <= cardsPerView) {
                prevBtn.style.display = 'none';
                nextBtn.style.display = 'none';
                return;
            } else {
                prevBtn.style.display = 'flex';
                nextBtn.style.display = 'flex';
            }

            function getCardsPerView() {
                if (window.innerWidth >= 1024) return 3;
                if (window.innerWidth >= 768) return 2;
                return 1;
            }

            function updateCarouselPosition() {
                // Make sure we have cards and they have width
                if (cards.length === 0 || !cards[0].offsetWidth) return;

                const cardWidth = cards[0].offsetWidth + 20; // card width + gap
                container.style.transform = `translateX(${-currentPosition * cardWidth}px)`;

                // Update button states based on position
                prevBtn.disabled = currentPosition === 0;
                prevBtn.style.opacity = currentPosition === 0 ? '0.5' : '1';

                const maxPosition = Math.max(0, totalCards - cardsPerView);
                nextBtn.disabled = currentPosition >= maxPosition;
                nextBtn.style.opacity = currentPosition >= maxPosition ? '0.5' : '1';
            }

            function slideNext() {
                const maxPosition = Math.max(0, totalCards - cardsPerView);
                if (currentPosition < maxPosition) {
                    currentPosition++;
                    updateCarouselPosition();
                }
            }

            function slidePrev() {
                if (currentPosition > 0) {
                    currentPosition--;
                    updateCarouselPosition();
                }
            }

            // Remove existing event listeners and add new ones
            prevBtn.replaceWith(prevBtn.cloneNode(true));
            nextBtn.replaceWith(nextBtn.cloneNode(true));

            const newPrevBtn = document.getElementById('activity-nav-prev');
            const newNextBtn = document.getElementById('activity-nav-next');

            newPrevBtn.addEventListener('click', slidePrev);
            newNextBtn.addEventListener('click', slideNext);

            // Initialize button states
            updateCarouselPosition();

            // Handle window resize events
            window.addEventListener('resize', () => {
                const newCardsPerView = getCardsPerView();
                if (newCardsPerView !== cardsPerView) {
                    cardsPerView = newCardsPerView;
                    currentPosition = Math.min(currentPosition, Math.max(0, totalCards - cardsPerView));
                }
                updateCarouselPosition();
            });
        }

        function loadActivityImage(imageSrc, imgElement) {
            if (imageSrc.startsWith('/')) {
                imageSrc = window.location.origin + imageSrc;
            }

            if (activityImageCache.has(imageSrc)) {
                if (activityImageCache.get(imageSrc) === 'error') {
                    return;
                } else {
                    imgElement.src = imageSrc;
                    return;
                }
            }

            const preloadImage = new Image();

            preloadImage.onload = function() {
                activityImageCache.set(imageSrc, 'loaded');
                imgElement.src = imageSrc;
            };

            preloadImage.onerror = function() {
                activityImageCache.set(imageSrc, 'error');
            };

            preloadImage.src = imageSrc;
        }
    </script>
</body>
