<html lang="en">
<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>MyKartavya FAQs</title>
    <!-- Poppins Font -->
    <link href="https://fonts.googleapis.com/css2?family=Poppins:wght@400;500;600&display=swap" rel="stylesheet">
    <!-- Tailwind CSS -->
    <script src="https://cdn.tailwindcss.com"></script>

    <style>
        * {
            font-family: 'Poppins', sans-serif;
            margin: 0;
            padding: 0;
            box-sizing: border-box;
        }

        body {
            background-color: #ffffff;
        }

        .page-container {
            max-width: 1200px;
            margin: 0 auto;
            padding: 40px 20px;
        }

        .faq-container {
            max-width: 900px;
            margin: 0 auto;
            height: 600px;
            overflow-y: auto;
            padding: 20px;
            position: relative;
            background-color: #ffffff;
        }

        /* Custom Scrollbar */
        .faq-container::-webkit-scrollbar {
            width: 6px;
        }

        .faq-container::-webkit-scrollbar-track {
            background: #f1f1f1;
            border-radius: 3px;
        }

        .faq-container::-webkit-scrollbar-thumb {
            background: #888;
            border-radius: 3px;
        }

        .faq-container::-webkit-scrollbar-thumb:hover {
            background: #555;
        }

        .faq-item {
            margin-bottom: 8px;
            border-radius: 4px;
            overflow: hidden;
        }

        .faq-button {
            width: 100%;
            text-align: left;
            padding: 16px 24px;
            background-color: #F8F9FA;
            border: none;
            display: flex;
            justify-content: space-between;
            align-items: center;
            cursor: pointer;
            transition: all 0.3s ease;
        }

        .question-text {
            font-weight: 500;
            color: #212529;
            flex-grow: 1;
            padding-right: 20px;
            font-size: 14px;
            text-transform: uppercase;
        }

        .plus-icon {
            width: 24px;
            height: 24px;
            position: relative;
            flex-shrink: 0;
        }

        .plus-icon::before,
        .plus-icon::after {
            content: '';
            position: absolute;
            background-color: #212529;
            transition: all 0.3s ease;
        }

        .plus-icon::before {
            width: 2px;
            height: 16px;
            top: 4px;
            left: 11px;
        }

        .plus-icon::after {
            width: 16px;
            height: 2px;
            top: 11px;
            left: 4px;
        }

        .faq-button.active {
            background-color: #212529;
        }

        .faq-button.active .question-text {
            color: #ffffff;
        }

        .faq-button.active .plus-icon::before,
        .faq-button.active .plus-icon::after {
            background-color: #ffffff;
        }

        .faq-button.active .plus-icon::before {
            transform: rotate(90deg);
        }

        .faq-answer {
            display: none;
            padding: 20px 24px;
            background-color: #ffffff;
            color: #212529;
            line-height: 1.6;
            font-size: 14px;
        }

        .faq-answer.active {
            display: block;
        }

        .faq-button:hover {
            background-color: #e9ecef;
        }

        .faq-button.active:hover {
            background-color: #212529;
        }

        /* Loading Spinner */
        .loading-spinner {
            position: absolute;
            top: 0;
            left: 0;
            right: 0;
            bottom: 0;
            background-color: #ffffff;
            display: flex;
            justify-content: center;
            align-items: center;
        }

        .spinner {
            width: 40px;
            height: 40px;
            border: 4px solid #f3f3f3;
            border-top: 4px solid #212529;
            border-radius: 50%;
            animation: spin 1s linear infinite;
        }

        @keyframes spin {
            0% { transform: rotate(0deg); }
            100% { transform: rotate(360deg); }
        }

        /* Error Message */
        .error-message {
            position: absolute;
            top: 0;
            left: 0;
            right: 0;
            bottom: 0;
            background-color: #ffffff;
            display: none;
            justify-content: center;
            align-items: center;
            color: #dc2626;
            text-align: center;
            padding: 20px;
        }
    </style>
</head>
<body>
    <div class="page-container">
        <div class="faq-container">
            <!-- Loading Spinner -->
            <div id="loading" class="loading-spinner">
                <div class="spinner"></div>
            </div>

            <!-- Error Message -->
            <div id="error" class="error-message">
                Failed to load FAQs. Please try again later.
            </div>

            <!-- FAQ List -->
            <div id="faq-list" class="space-y-2" style="display: none;">
                <!-- FAQs will be dynamically inserted here -->
            </div>
        </div>
    </div>

    <script>
        document.addEventListener('DOMContentLoaded', function() {
            const faqList = document.getElementById('faq-list');
            const loading = document.getElementById('loading');
            const error = document.getElementById('error');

            async function createFaqElement(faq) {
                const faqItem = document.createElement('div');
                faqItem.className = 'faq-item';
                faqItem.innerHTML = `
                    <button class="faq-button">
                        <span class="question-text">${faq.question}</span>
                        <div class="plus-icon"></div>
                    </button>
                    <div class="faq-answer">
                        <p>${faq.answer}</p>
                    </div>
                `;
                return faqItem;
            }

            async function initializeFAQs() {
                try {
                    const response = await fetch('/api/method/mykartavya.controllers.api.get_faqs');
                    const data = await response.json();

                    if (data.message.success) {
                        const faqs = data.message.data.all_faqs;

                        // Create and append FAQ elements
                        const faqPromises = faqs.map(createFaqElement);
                        const faqElements = await Promise.all(faqPromises);

                        // Clear existing content
                        faqList.innerHTML = '';

                        // Append all FAQ elements
                        faqElements.forEach(element => {
                            faqList.appendChild(element);
                        });

                        // Add click event listeners
                        document.querySelectorAll('.faq-button').forEach(button => {
                            button.addEventListener('click', () => {
                                // Close all other FAQs
                                document.querySelectorAll('.faq-button').forEach(otherButton => {
                                    if (otherButton !== button) {
                                        otherButton.classList.remove('active');
                                        otherButton.nextElementSibling.classList.remove('active');
                                    }
                                });

                                // Toggle current FAQ
                                button.classList.toggle('active');
                                button.nextElementSibling.classList.toggle('active');

                                // Scroll into view if needed
                                if (button.classList.contains('active')) {
                                    const containerTop = document.querySelector('.faq-container').offsetTop;
                                    const buttonTop = button.offsetTop;
                                    const containerScrollTop = document.querySelector('.faq-container').scrollTop;

                                    if (buttonTop < containerScrollTop || buttonTop > containerScrollTop + 500) {
                                        button.scrollIntoView({ behavior: 'smooth', block: 'nearest' });
                                    }
                                }
                            });
                        });

                        // Show FAQ list and hide loading spinner
                        loading.style.display = 'none';
                        faqList.style.display = 'block';
                    } else {
                        throw new Error('Failed to fetch FAQs');
                    }
                } catch (err) {
                    console.error('Error:', err);
                    loading.style.display = 'none';
                    error.style.display = 'flex';
                }
            }

            // Initialize FAQs
            initializeFAQs();
        });
    </script>
</body>
