<html lang="en">
<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>Volunteer Page</title>
    <script src="https://cdn.tailwindcss.com"></script>
    <!-- Poppins font -->
    <link href="https://fonts.googleapis.com/css2?family=Poppins:wght@400;600&display=swap" rel="stylesheet">
    <script>
        tailwind.config = {
            theme: {
                extend: {
                    colors: {
                        custom: {
                            pink: '#E23D90',
                            lightpink: '#F9D8E9',
                            bg: '#FDEDF3'
                        }
                    },
                    fontFamily: {
                        'poppins': ['Poppins', 'sans-serif'],
                    }
                }
            }
        }
    </script>
    <style>
        body {
            font-family: 'Poppins', sans-serif;
            background-color: #FDEDF3;
        }

        .brush-stroke {
            position: relative;
            background-image: url('https://res.cloudinary.com/dyt5jqnax/image/upload/v1742199581/image-removebg-preview_emdhbb.png');
            background-repeat: no-repeat;
            background-size: 100% 100%;
            padding: 10px 20px;
            display: inline-block;
            margin: 0 auto;
        }

        .brush-stroke::after {
            content: '';
            position: absolute;
            top: 0;
            left: 0;
            width: 100%;
            height: 100%;
            background-image: url('https://res.cloudinary.com/dyt5jqnax/image/upload/v1742200447/image_if2aje.png');
            background-repeat: no-repeat;
            background-size: 100% 100%;
            opacity: 0.3;
            pointer-events: none;
        }

        .heading-text {
            font-weight: 600;
            font-size: 47px;
            line-height: 140%;
            letter-spacing: 0%;
            text-align: center;
        }

        .paragraph-text {
            font-weight: 400;
            font-size: 16px;
            line-height: 150%;
            letter-spacing: 0.5%;
            text-align: center;
        }

        .learn-more-btn {
            padding: 0 24px;
            height: 40px;
            border: 1px solid #E23D90;
            color: #374151;
            transition: all 0.3s;
            border-radius: 4px;
            display: inline-flex;
            align-items: center;
            justify-content: center;
        }

        .learn-more-btn:hover {
            background-color: #F9D8E9;
        }

        @media (max-width: 768px) {
            .heading-text {
                font-size: 32px;
            }

            .brush-stroke {
                padding: 5px 15px;
            }
        }

        @media (max-width: 480px) {
            .heading-text {
                font-size: 24px;
            }

            .brush-stroke {
                padding: 5px 10px;
            }

            .paragraph-text {
                font-size: 14px;
            }
        }

        @media (min-width: 640px) {
            .learn-more-btn {
                padding: 0 32px;
                height: 48px;
            }
        }
    </style>
</head>
<body>
    <div class="flex justify-center w-full bg-custom-bg">
        <div class="w-full max-w-[1920px] h-[484px] px-4 py-8 md:px-6 lg:px-8">
            <div class="max-w-4xl w-full mx-auto h-full flex flex-col">
                <!-- Button container -->
                <div class="flex justify-center gap-2 sm:gap-4 mb-6 md:mb-8 flex-wrap">
                    <button id="btn-little-act" class="px-3 sm:px-4 h-6 rounded-full bg-custom-pink text-white border border-custom-pink hover:bg-custom-pink hover:text-white transition duration-300 text-xs sm:text-sm whitespace-nowrap">LITTLE ACT MATTERS</button>
                    <button id="btn-donate" class="px-3 sm:px-4 h-6 rounded-full bg-white text-custom-pink border border-custom-pink hover:bg-custom-pink hover:text-white transition duration-300 text-xs sm:text-sm whitespace-nowrap">DONATE YOUR SKILL</button>
                    <button id="btn-volunteer" class="px-3 sm:px-4 h-6 rounded-full bg-white text-custom-pink border border-custom-pink hover:bg-custom-pink hover:text-white transition duration-300 text-xs sm:text-sm whitespace-nowrap">VOLUNTEER & GIVE BACK</button>
                </div>

                <!-- Content container -->
                <div id="content-container" class="flex-1 flex flex-col justify-between">
                    <!-- Little Act Matters Content -->
                    <div id="content-little-act" class="h-full flex flex-col justify-between">
                        <div class="text-center">
                            <h1 class="heading-text text-gray-800 mb-2 md:mb-4">Join us to spread joy and</h1>
                            <div class="flex justify-center mb-4 md:mb-8">
                                <h1 class="heading-text text-white brush-stroke">make a difference</h1>
                            </div>
                            <p class="paragraph-text text-gray-600 mb-6 md:mb-8 max-w-2xl mx-auto px-2">
                                Volunteer for activities that bring joy, support, and transformation to those in need.
                                Because together, our little acts create a ripple effect of kindness and positivity
                            </p>
                        </div>
                        <div class="flex justify-center mb-4">
                            <a href="/about" class="learn-more-btn">
                                LEARN MORE <span class="ml-2">→</span>
                            </a>
                        </div>
                    </div>

                    <!-- Donate Your Skill Content -->
                    <div id="content-donate" class="hidden h-full flex flex-col justify-between">
                        <div class="text-center">
                            <h1 class="heading-text text-gray-800 mb-2 md:mb-4">Share your talents and</h1>
                            <div class="flex justify-center mb-4 md:mb-8">
                                <h1 class="heading-text text-white brush-stroke">empower others</h1>
                            </div>
                            <p class="paragraph-text text-gray-600 mb-6 md:mb-8 max-w-2xl mx-auto px-2">
                                Your skills can make a world of difference to someone in need.
                                Whether it's teaching, mentoring, or creating, your expertise helps build stronger communities
                            </p>
                        </div>
                        <div class="flex justify-center mb-4">
                            <a href="/about" class="learn-more-btn">
                                LEARN MORE <span class="ml-2">→</span>
                            </a>
                        </div>
                    </div>

                    <!-- Volunteer & Give Back Content -->
                    <div id="content-volunteer" class="hidden h-full flex flex-col justify-between">
                        <div class="text-center">
                            <h1 class="heading-text text-gray-800 mb-2 md:mb-4">Contribute your time and</h1>
                            <div class="flex justify-center mb-4 md:mb-8">
                                <h1 class="heading-text text-white brush-stroke">transform lives</h1>
                            </div>
                            <p class="paragraph-text text-gray-600 mb-6 md:mb-8 max-w-2xl mx-auto px-2">
                                Your presence and efforts can brighten someone's day and provide essential support.
                                Join our community of volunteers dedicated to creating positive change through service
                            </p>
                        </div>
                        <div class="flex justify-center mb-4">
                            <a href="/about" class="learn-more-btn">
                                LEARN MORE <span class="ml-2">→</span>
                            </a>
                        </div>
                    </div>
                </div>

                <!-- Pagination dots -->
                <div class="flex justify-center mt-2 md:mt-4 space-x-2">
                    <div id="dot-1" class="w-8 h-2 bg-custom-pink rounded"></div>
                    <div id="dot-2" class="w-2 h-2 bg-gray-300 rounded-full"></div>
                    <div id="dot-3" class="w-2 h-2 bg-gray-300 rounded-full"></div>
                </div>
            </div>
        </div>
    </div>

    <script>
        document.addEventListener('DOMContentLoaded', function() {
            const btnLittleAct = document.getElementById('btn-little-act');
            const btnDonate = document.getElementById('btn-donate');
            const btnVolunteer = document.getElementById('btn-volunteer');

            const contentLittleAct = document.getElementById('content-little-act');
            const contentDonate = document.getElementById('content-donate');
            const contentVolunteer = document.getElementById('content-volunteer');

            const dot1 = document.getElementById('dot-1');
            const dot2 = document.getElementById('dot-2');
            const dot3 = document.getElementById('dot-3');

            function resetAll() {
                [btnLittleAct, btnDonate, btnVolunteer].forEach(btn => {
                    btn.className = "px-3 sm:px-4 h-6 rounded-full bg-white text-custom-pink border border-custom-pink hover:bg-custom-pink hover:text-white transition duration-300 text-xs sm:text-sm whitespace-nowrap";
                });

                contentLittleAct.classList.add('hidden');
                contentDonate.classList.add('hidden');
                contentVolunteer.classList.add('hidden');

                [dot1, dot2, dot3].forEach(dot => {
                    dot.className = "w-2 h-2 bg-gray-300 rounded-full";
                });
            }

            function showLittleAct() {
                resetAll();
                btnLittleAct.className = "px-3 sm:px-4 h-6 rounded-full bg-custom-pink text-white border border-custom-pink hover:bg-custom-pink hover:text-white transition duration-300 text-xs sm:text-sm whitespace-nowrap";
                contentLittleAct.classList.remove('hidden');
                dot1.className = "w-8 h-2 bg-custom-pink rounded";
            }

            function showDonate() {
                resetAll();
                btnDonate.className = "px-3 sm:px-4 h-6 rounded-full bg-custom-pink text-white border border-custom-pink hover:bg-custom-pink hover:text-white transition duration-300 text-xs sm:text-sm whitespace-nowrap";
                contentDonate.classList.remove('hidden');
                dot2.className = "w-8 h-2 bg-custom-pink rounded";
            }

            function showVolunteer() {
                resetAll();
                btnVolunteer.className = "px-3 sm:px-4 h-6 rounded-full bg-custom-pink text-white border border-custom-pink hover:bg-custom-pink hover:text-white transition duration-300 text-xs sm:text-sm whitespace-nowrap";
                contentVolunteer.classList.remove('hidden');
                dot3.className = "w-8 h-2 bg-custom-pink rounded";
            }

            showLittleAct();

            btnLittleAct.addEventListener('click', showLittleAct);
            btnDonate.addEventListener('click', showDonate);
            btnVolunteer.addEventListener('click', showVolunteer);
        });
    </script>
</body>
