    <script>
        const imageCache = new Map();
        let apiCallInProgress = false;
        let activitiesData = null;

        document.addEventListener('DOMContentLoaded', function() {
            fetchActivities();
        });

        function getInitials(name) {
            return name
                .split(' ')
                .map(word => word[0])
                .join('')
                .slice(0, 2);
        }

        function createAvatarElement(volunteer) {
            if (volunteer.user_image) {
                const img = document.createElement('img');
                img.className = 'w-8 h-8 rounded-full';
                img.src = '/api/placeholder/32/32';
                img.alt = volunteer.full_name || 'Volunteer';
                loadImage(volunteer.user_image, img);
                return img;
            } else {
                const div = document.createElement('div');
                div.className = 'avatar-initials';
                div.textContent = getInitials(volunteer.full_name || 'User');
                return div;
            }
        }

        function fetchActivities() {
            if (apiCallInProgress) return;
            apiCallInProgress = true;

            document.getElementById('loading').style.display = 'block';
            document.getElementById('error').classList.add('hidden');

            if (activitiesData) {
                renderCards(activitiesData);
                document.getElementById('loading').style.display = 'none';
                apiCallInProgress = false;
                return;
            }

            fetch('/api/method/mykartavya.controllers.api.available_commitments_public')
                .then(response => {
                    if (!response.ok) {
                        throw new Error('Network response was not ok');
                    }
                    return response.json();
                })
                .then(data => {
                    if (data && data.message && Array.isArray(data.message)) {
                        activitiesData = data.message;
                        renderCards(activitiesData);
                    } else {
                        throw new Error('Invalid data format');
                    }
                    document.getElementById('loading').style.display = 'none';
                    apiCallInProgress = false;
                })
                .catch(error => {
                    console.error('Error fetching activities:', error);
                    document.getElementById('loading').style.display = 'none';
                    document.getElementById('error').classList.remove('hidden');
                    apiCallInProgress = false;
                });
        }

        function renderCards(activities) {
            const cardsContainer = document.getElementById('cardsContainer');
            const cardTemplate = document.getElementById('cardTemplate');
            const carouselContainer = document.querySelector('.carousel-container');
            const prevBtn = document.getElementById('volunteer_card_prev');
            const nextBtn = document.getElementById('volunteer_card_next');

            cardsContainer.innerHTML = '';

            if (activities.length === 0) {
                cardsContainer.innerHTML = '<p class="text-center text-gray-500 text-lg">No activities available at the moment.</p>';
                prevBtn.style.display = 'none';
                nextBtn.style.display = 'none';
                return;
            }

            if (activities.length === 1) {
                prevBtn.style.display = 'none';
                nextBtn.style.display = 'none';
                cardsContainer.className = 'single-card-container';
                const card = cardTemplate.content.cloneNode(true);
                const cardElement = card.querySelector('.card');
                cardElement.className = 'single-card';
                renderCardContent(card, activities[0]);
                cardsContainer.appendChild(card);
                return;
            }

            prevBtn.style.display = 'flex';
            nextBtn.style.display = 'flex';
            cardsContainer.className = 'card-container';

            activities.forEach(activity => {
                const card = cardTemplate.content.cloneNode(true);
                renderCardContent(card, activity);
                cardsContainer.appendChild(card);
            });

            initializeCarousel();
        }

        function renderCardContent(card, activity) {
            let volunteers = [];
            try {
                volunteers = JSON.parse(activity.volunteers || '[]');
            } catch (e) {
                console.error('Error parsing volunteers:', e);
            }

            const activityImage = card.querySelector('#activityImage');
            const defaultImageContainer = activityImage.closest('.default-image-container');
            const defaultImage = 'https://res.cloudinary.com/dyt5jqnax/image/upload/v1742968038/mykartavya-logo_jptv31.png';

            if (activity.activity_image) {
                loadImage(activity.activity_image, activityImage, () => {
                    activityImage.classList.remove('default-image');
                    activityImage.classList.add('card-image');
                    defaultImageContainer.classList.remove('default-image-container');
                });
            } else {
                activityImage.src = defaultImage;
            }

            card.querySelector('#activityType').textContent = activity.activity_type || 'Activity';
            card.querySelector('#karmaPoints').textContent = activity.karma_points || 0;
            card.querySelector('#activityTitle').textContent = activity.title || 'Untitled Activity';

            const tempDiv = document.createElement('div');
            tempDiv.innerHTML = activity.activity_description || '';
            let description = tempDiv.textContent || tempDiv.innerText || '';
            if (description.length > 100) {
                description = description.substring(0, 100) + '...';
            }
            card.querySelector('#activityDescription').textContent = description;

            // Add SDG badges
            const sdgBadgesContainer = card.querySelector('#sdgBadges');
            let sdgs = [];
            try {
                sdgs = JSON.parse(activity.sdgs || '[]');
            } catch (e) {
                console.error('Error parsing SDGs:', e);
            }

            sdgs.forEach(sdg => {
                const sdgBadge = document.createElement('div');
                sdgBadge.className = 'sdg-badge';

                const img = document.createElement('img');
                img.src = '/api/placeholder/32/32';
                img.alt = sdg.sdgs_name;

                if (sdg.image) {
                    loadImage(sdg.image, img);
                }

                sdgBadge.appendChild(img);
                sdgBadgesContainer.appendChild(sdgBadge);
            });

            try {
                const startDate = activity.start_date ? new Date(activity.start_date) : new Date();
                const endDate = activity.end_date ? new Date(activity.end_date) : new Date();

                const formatDate = (date) => {
                    return `${date.getDate()} ${date.toLocaleString('default', { month: 'short' })}, ${date.getFullYear()}`;
                };

                card.querySelector('#dateRange').textContent = `${formatDate(startDate)} - ${formatDate(endDate)}`;
            } catch (e) {
                console.error('Error formatting dates:', e);
                card.querySelector('#dateRange').textContent = 'Date not available';
            }

            card.querySelector('#hours').textContent = activity.hours || 0;

            const volunteersGroup = card.querySelector('#volunteersGroup');
            const moreVolunteers = card.querySelector('#moreVolunteers');

            const maxDisplayedVolunteers = 3;
            for (let i = 0; i < Math.min(volunteers.length, maxDisplayedVolunteers); i++) {
                const volunteer = volunteers[i];
                const avatarElement = createAvatarElement(volunteer);
                volunteersGroup.appendChild(avatarElement);
            }

            if (volunteers.length > maxDisplayedVolunteers) {
                moreVolunteers.textContent = `+${volunteers.length - maxDisplayedVolunteers} more`;
            }

            const actNowBtn = card.querySelector('button');
            actNowBtn.addEventListener('click', function() {
                window.location.href = '/frontend/login';
            });
        }

        function initializeCarousel() {
            const container = document.querySelector('.card-container');
            const prevBtn = document.getElementById('volunteer_card_prev');
            const nextBtn = document.getElementById('volunteer_card_next');
            const cards = document.querySelectorAll('.card');

            if (!container || container.className === 'single-card-container') {
                return;
            }

            let currentPosition = 0;
            let cardsPerView = getCardsPerView();
            const totalCards = cards.length;

            if (totalCards <= cardsPerView) {
                prevBtn.style.display = 'none';
                nextBtn.style.display = 'none';
                return;
            } else {
                prevBtn.style.display = 'flex';
                nextBtn.style.display = 'flex';
            }

            function getCardsPerView() {
                if (window.innerWidth >= 1024) return 3;
                if (window.innerWidth >= 768) return 2;
                return 1;
            }

            function updateCarouselPosition() {
                if (cards.length === 0 || !cards[0].offsetWidth) return;

                const cardWidth = cards[0].offsetWidth + 20;
                container.style.transform = `translateX(${-currentPosition * cardWidth}px)`;

                prevBtn.disabled = currentPosition === 0;
                prevBtn.style.opacity = currentPosition === 0 ? '0.5' : '1';

                const maxPosition = Math.max(0, totalCards - cardsPerView);
                nextBtn.disabled = currentPosition >= maxPosition;
                nextBtn.style.opacity = currentPosition >= maxPosition ? '0.5' : '1';
            }

            function slideNext() {
                const maxPosition = Math.max(0, totalCards - cardsPerView);
                if (currentPosition < maxPosition) {
                    currentPosition++;
                    updateCarouselPosition();
                }
            }

            function slidePrev() {
                if (currentPosition > 0) {
                    currentPosition--;
                    updateCarouselPosition();
                }
            }

            prevBtn.replaceWith(prevBtn.cloneNode(true));
            nextBtn.replaceWith(nextBtn.cloneNode(true));

            const newPrevBtn = document.getElementById('volunteer_card_prev');
            const newNextBtn = document.getElementById('volunteer_card_next');

            newPrevBtn.addEventListener('click', slidePrev);
            newNextBtn.addEventListener('click', slideNext);

            updateCarouselPosition();

            window.addEventListener('resize', () => {
                const newCardsPerView = getCardsPerView();
                if (newCardsPerView !== cardsPerView) {
                    cardsPerView = newCardsPerView;
                    currentPosition = Math.min(currentPosition, Math.max(0, totalCards - cardsPerView));
                }
                updateCarouselPosition();
            });
        }

        function loadImage(imageSrc, imgElement, onLoadCallback) {
            if (imageSrc.startsWith('/')) {
                imageSrc = window.location.origin + imageSrc;
            }

            if (imageCache.has(imageSrc)) {
                if (imageCache.get(imageSrc) === 'error') {
                    return;
                } else {
                    imgElement.src = imageSrc;
                    if (onLoadCallback) onLoadCallback();
                    return;
                }
            }

            const preloadImage = new Image();

            preloadImage.onload = function() {
                imageCache.set(imageSrc, 'loaded');
                imgElement.src = imageSrc;
                if (onLoadCallback) onLoadCallback();
            };

            preloadImage.onerror = function() {
                imageCache.set(imageSrc, 'error');
                imgElement.src = 'https://res.cloudinary.com/dyt5jqnax/image/upload/v1742968038/mykartavya-logo_jptv31.png';
            };

            preloadImage.src = imageSrc;
        }
    </script>
