        }

        .custom_abc_single-card-container {
            display: flex;
            justify-content: center;
            width: 100%;
        }

        .custom_abc_single-card {
            width: 100%;
            max-width: 448px;
        }

        @media (max-width: 1024px) {
            .custom_abc_card {
                width: calc(50% - 20px);
            }
        }

        @media (max-width: 768px) {
            .custom_abc_card {
                width: calc(100% - 20px);
            }
            .custom_abc_date-time-info {
                font-size: 14px;
                flex-direction: row;
                align-items: center;
            }
        }
    </style>
</head>
<body>
    <div class="custom_abc_max-container">
        <div id="custom_abc_loading" class="custom_abc_loader" aria-label="Loading activities"></div>
        <div class="custom_abc_carousel-container">
            <button class="custom_abc_nav-prev" id="custom_abc_nav-prev" aria-label="Previous activities">
                <svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" fill="none" viewBox="0 0 24 24" stroke="currentColor">
