                <div class="custom_abc_sdg-badges" id="custom_abc_sdg-badges"></div>
            </div>
            <div class="p-4 sm:p-6">
                <h2 class="mb-2 custom_abc_activity-title" id="custom_abc_title"></h2>
                <p class="mb-4 custom_abc_activity-description" id="custom_abc_description"></p>
                <div class="mt-4 custom_abc_date-time-info">
                    <div class="flex items-center">
                        <svg xmlns="http://www.w3.org/2000/svg" class="mr-1 h-4 w-4" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                            <path stroke-linecap="round" stroke-linejoin="round" stroke-width="2" d="M8 7V3m8 4V3m-9 8h10M5 21h14a2 2 0 002-2V7a2 2 0 00-2-2H5a2 2 0 00-2 2v12a2 2 0 002 2z" />
