<script>
    // Constants for default and configuration
    const CUSTOM_ABC_DEFAULT_IMAGE = 'https://res.cloudinary.com/dyt5jqnax/image/upload/v1742968038/mykartavya-logo_jptv31.png';
    const CUSTOM_ABC_MAX_IMAGE_CACHE_SIZE = 100;
    const CUSTOM_ABC_CARDS_PER_VIEW = {
        desktop: 3,
        tablet: 2,
        mobile: 1
    };

    // Image caching mechanism
    class ImageCache {
        constructor(maxSize = 100) {
            this.cache = new Map();
            this.maxSize = maxSize;
        }

        get(key) {
            const cachedImage = this.cache.get(key);
            if (cachedImage) {
                this.cache.delete(key);
                this.cache.set(key, cachedImage);
            }
            return cachedImage;
        }

        set(key, value) {
            if (this.cache.size >= this.maxSize) {
                const oldestKey = this.cache.keys().next().value;
                this.cache.delete(oldestKey);
            }
            this.cache.set(key, value);
        }

        has(key) {
            return this.cache.has(key);
        }
    }

    // Global variables
    let custom_abc_apiCallInProgress = false;
    let custom_abc_activityData = null;
    const custom_abc_imageCache = new ImageCache(CUSTOM_ABC_MAX_IMAGE_CACHE_SIZE);

    // Utility functions
    function custom_abc_getInitials(name) {
        return name
            .split(' ')
            .map(word => word[0])
            .join('')
            .slice(0, 2)
            .toUpperCase();
    }

    function custom_abc_createAvatarElement(volunteer) {
        if (volunteer.user_image) {
            const img = document.createElement('img');
            img.className = 'w-8 h-8 rounded-full';
            img.alt = volunteer.full_name || 'Volunteer';
            custom_abc_loadImage(volunteer.user_image, img);
            return img;
        } else {
            const div = document.createElement('div');
            div.className = 'custom_abc_avatar-initials';
            div.textContent = custom_abc_getInitials(volunteer.full_name || 'User');
            return div;
        }
    }

    function custom_abc_loadImage(imageSrc, imgElement, onLoadCallback) {
        if (imageSrc.startsWith('/')) {
            imageSrc = window.location.origin + imageSrc;
        }

        if (custom_abc_imageCache.has(imageSrc)) {
            const cachedResult = custom_abc_imageCache.get(imageSrc);
            if (cachedResult === 'error') {
                imgElement.src = CUSTOM_ABC_DEFAULT_IMAGE;
                imgElement.classList.add('custom_abc_default-image');
            } else {
                imgElement.src = imageSrc;
                if (onLoadCallback) onLoadCallback();
            }
            return;
        }

        const preloadImage = new Image();

        preloadImage.onload = function() {
            custom_abc_imageCache.set(imageSrc, 'loaded');
            imgElement.src = imageSrc;
            if (onLoadCallback) onLoadCallback();
        };

        preloadImage.onerror = function() {
            custom_abc_imageCache.set(imageSrc, 'error');
            imgElement.src = CUSTOM_ABC_DEFAULT_IMAGE;
            imgElement.classList.add('custom_abc_default-image');
        };

        preloadImage.src = imageSrc;
    }

    function custom_abc_fetchActivities() {
        if (custom_abc_apiCallInProgress) return;
        custom_abc_apiCallInProgress = true;

        const loadingElement = document.getElementById('custom_abc_loading');
        const errorElement = document.getElementById('custom_abc_error');

        loadingElement.style.display = 'block';
        errorElement.classList.add('hidden');

        if (custom_abc_activityData) {
            custom_abc_renderCards(custom_abc_activityData);
            loadingElement.style.display = 'none';
            custom_abc_apiCallInProgress = false;
            return;
        }

        fetch('/api/method/mykartavya.controllers.api.available_commitments_public')
            .then(response => {
                if (!response.ok) throw new Error('Network response was not ok');
                return response.json();
            })
            .then(data => {
                if (data && data.message && Array.isArray(data.message)) {
                    custom_abc_activityData = data.message;
                    custom_abc_renderCards(custom_abc_activityData);
                } else {
                    throw new Error('Invalid data format');
                }
                loadingElement.style.display = 'none';
                custom_abc_apiCallInProgress = false;
            })
            .catch(error => {
                console.error('Error fetching activities:', error);
                loadingElement.style.display = 'none';
                errorElement.classList.remove('hidden');
                custom_abc_apiCallInProgress = false;
            });
    }

    function custom_abc_renderCards(activities) {
        const cardsContainer = document.getElementById('custom_abc_cards-container');
        const cardTemplate = document.getElementById('custom_abc_card-template');
        const prevBtn = document.getElementById('custom_abc_nav-prev');
        const nextBtn = document.getElementById('custom_abc_nav-next');

        cardsContainer.innerHTML = '';

        if (activities.length === 0) {
            cardsContainer.innerHTML = '<p class="text-center text-gray-500 text-lg">No activities available at the moment.</p>';
            prevBtn.style.display = 'none';
            nextBtn.style.display = 'none';
            return;
        }

        if (activities.length === 1) {
            prevBtn.style.display = 'none';
            nextBtn.style.display = 'none';
            cardsContainer.className = 'custom_abc_single-card-container';
            const card = cardTemplate.content.cloneNode(true);
            const cardElement = card.querySelector('.custom_abc_card');
            cardElement.className = 'custom_abc_single-card';
            custom_abc_renderCardContent(card, activities[0]);
            cardsContainer.appendChild(card);
            return;
        }

        prevBtn.style.display = 'flex';
        nextBtn.style.display = 'flex';
        cardsContainer.className = 'custom_abc_card-container';

        activities.forEach(activity => {
            const card = cardTemplate.content.cloneNode(true);
            custom_abc_renderCardContent(card, activity);
            cardsContainer.appendChild(card);
        });

        custom_abc_initializeCarousel();
    }

    function custom_abc_renderCardContent(card, activity) {
        const activityImage = card.querySelector('#custom_abc_image');
        const defaultImageContainer = activityImage.closest('.custom_abc_default-image-container');

        if (activity.activity_image) {
            custom_abc_loadImage(activity.activity_image, activityImage, () => {
                activityImage.classList.remove('custom_abc_default-image');
                activityImage.classList.add('custom_abc_card-image');
                defaultImageContainer.classList.remove('custom_abc_default-image-container');
            });
        } else {
            activityImage.src = CUSTOM_ABC_DEFAULT_IMAGE;
            activityImage.classList.add('custom_abc_default-image');
        }

        card.querySelector('#custom_abc_type').textContent = activity.activity_type || 'Activity';
        card.querySelector('#custom_abc_points').textContent = activity.karma_points || 0;
        card.querySelector('#custom_abc_title').textContent = activity.title || 'Untitled Activity';

        const tempDiv = document.createElement('div');
        tempDiv.innerHTML = activity.activity_description || '';
        let description = tempDiv.textContent || tempDiv.innerText || '';
        if (description.length > 100) {
            description = description.substring(0, 100) + '...';
        }
        card.querySelector('#custom_abc_description').textContent = description;

        // Handle SDG badges
        const sdgBadgesContainer = card.querySelector('#custom_abc_sdg-badges');
        let sdgs = [];
        try {
            sdgs = JSON.parse(activity.sdgs || '[]');
        } catch (e) {
            console.error('Error parsing SDGs:', e);
        }

        sdgs.forEach(sdg => {
            const sdgBadge = document.createElement('div');
            sdgBadge.className = 'custom_abc_sdg-badge';

            const img = document.createElement('img');
            img.alt = sdg.sdgs_name || 'SDG';

            if (sdg.image) {
                custom_abc_loadImage(sdg.image, img);
            }

            sdgBadge.appendChild(img);
            sdgBadgesContainer.appendChild(sdgBadge);
        });

        // Format and set dates
        try {
            const startDate = activity.start_date ? new Date(activity.start_date) : new Date();
            const endDate = activity.end_date ? new Date(activity.end_date) : new Date();

            const formatDate = (date) => {
                return `${date.getDate()} ${date.toLocaleString('default', { month: 'short' })}, ${date.getFullYear()}`;
            };

            card.querySelector('#custom_abc_date-range').textContent = `${formatDate(startDate)} - ${formatDate(endDate)}`;
        } catch (e) {
            console.error('Error formatting dates:', e);
            card.querySelector('#custom_abc_date-range').textContent = 'Date not available';
        }

        card.querySelector('#custom_abc_hours').textContent = activity.hours || 0;

        // Handle volunteers
        const volunteersGroup = card.querySelector('#custom_abc_volunteers-group');
        const moreVolunteers = card.querySelector('#custom_abc_more-volunteers');
        let volunteers = [];
        try {
            volunteers = JSON.parse(activity.volunteers || '[]');
        } catch (e) {
            console.error('Error parsing volunteers:', e);
        }

        const maxDisplayedVolunteers = 3;
        for (let i = 0; i < Math.min(volunteers.length, maxDisplayedVolunteers); i++) {
            volunteersGroup.appendChild(custom_abc_createAvatarElement(volunteers[i]));
        }

        if (volunteers.length > maxDisplayedVolunteers) {
            moreVolunteers.textContent = `+${volunteers.length - maxDisplayedVolunteers} more`;
        }

        // Add help button click handler
        const helpButton = card.querySelector('button');
        helpButton.addEventListener('click', () => {
            window.location.href = '/frontend/login';
        });
    }

    function custom_abc_initializeCarousel() {
        const container = document.querySelector('.custom_abc_card-container');
        const prevBtn = document.getElementById('custom_abc_nav-prev');
        const nextBtn = document.getElementById('custom_abc_nav-next');
        const cards = document.querySelectorAll('.custom_abc_card');

        if (!container || container.className === 'custom_abc_single-card-container') {
            return;
        }

        let currentPosition = 0;
        let cardsPerView = custom_abc_getCardsPerView();
        const totalCards = cards.length;

        if (totalCards <= cardsPerView) {
            prevBtn.style.display = 'none';
            nextBtn.style.display = 'none';
            return;
        }

        function custom_abc_getCardsPerView() {
            const width = window.innerWidth;
            if (width >= 1024) return CUSTOM_ABC_CARDS_PER_VIEW.desktop;
            if (width >= 768) return CUSTOM_ABC_CARDS_PER_VIEW.tablet;
            return CUSTOM_ABC_CARDS_PER_VIEW.mobile;
        }

        function custom_abc_updateCarouselPosition() {
            if (!cards.length || !cards[0].offsetWidth) return;

            const cardWidth = cards[0].offsetWidth + 20;
            container.style.transform = `translateX(${-currentPosition * cardWidth}px)`;

            prevBtn.disabled = currentPosition === 0;
            prevBtn.style.opacity = currentPosition === 0 ? '0.5' : '1';

            const maxPosition = Math.max(0, totalCards - cardsPerView);
            nextBtn.disabled = currentPosition >= maxPosition;
            nextBtn.style.opacity = currentPosition >= maxPosition ? '0.5' : '1';
        }

        function custom_abc_slideNext() {
            const maxPosition = Math.max(0, totalCards - cardsPerView);
            if (currentPosition < maxPosition) {
                currentPosition++;
                custom_abc_updateCarouselPosition();
            }
        }

        function custom_abc_slidePrev() {
            if (currentPosition > 0) {
                currentPosition--;
                custom_abc_updateCarouselPosition();
            }
        }

        prevBtn.replaceWith(prevBtn.cloneNode(true));
        nextBtn.replaceWith(nextBtn.cloneNode(true));

        const newPrevBtn = document.getElementById('custom_abc_nav-prev');
        const newNextBtn = document.getElementById('custom_abc_nav-next');

        newPrevBtn.addEventListener('click', custom_abc_slidePrev);
        newNextBtn.addEventListener('click', custom_abc_slideNext);

        custom_abc_updateCarouselPosition();

        window.addEventListener('resize', () => {
            const newCardsPerView = custom_abc_getCardsPerView();
            if (newCardsPerView !== cardsPerView) {
                cardsPerView = newCardsPerView;
                currentPosition = Math.min(currentPosition, Math.max(0, totalCards - cardsPerView));
            }
            custom_abc_updateCarouselPosition();
        });
    }

    // Initialize on DOM load
    document.addEventListener('DOMContentLoaded', function() {
        custom_abc_fetchActivities();
    });
</script>
</body>
</html>

