<body>
    <div class="parent-container min-h-screen">
        <div class="container mx-auto px-4 py-16">
            <!-- Testimonials Header -->
            <div class="text-center mb-12">
                <p class="text-orange-500 font-medium mb-2">Testimonials</p>
                <h2 class="font-bold text-gray-900" style="font-size: 23px;">What People Are Saying About Us</h2>
            </div>

            <!-- Loading Spinner -->
            <div id="customLoadingSpinner" class="flex justify-center items-center py-16">
                <div class="custom-loader">
                    <div></div>
                    <div></div>
                    <div></div>
                    <div></div>
                </div>
            </div>

            <!-- Testimonials Carousel -->
            <div id="testimonialsCarousel" class="relative hidden">
                <div class="testimonial-container">
                    <div class="testimonial-slider" id="testimonialSlider">
                        <!-- Testimonials will be added here dynamically -->
                    </div>
                </div>

                <!-- Navigation Arrows -->
                <button id="custom_prev" class="nav-button absolute left-0 top-1/2 transform -translate-y-1/2 hidden z-10 ml-2">
                    <svg xmlns="http://www.w3.org/2000/svg" class="h-6 w-6 text-gray-500" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                        <path stroke-linecap="round" stroke-linejoin="round" stroke-width="2" d="M15 19l-7-7 7-7" />
                    </svg>
                </button>
                <button id="custom_next" class="nav-button absolute right-0 top-1/2 transform -translate-y-1/2 hidden z-10 mr-2">
                    <svg xmlns="http://www.w3.org/2000/svg" class="h-6 w-6 text-gray-500" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                        <path stroke-linecap="round" stroke-linejoin="round" stroke-width="2" d="M9 5l7 7-7 7" />
                    </svg>
                </button>
            </div>
        </div>
    </div>

    <script>
        document.addEventListener('DOMContentLoaded', function() {
            const slider = document.getElementById('testimonialSlider');
            const prevBtn = document.getElementById('custom_prev');
            const nextBtn = document.getElementById('custom_next');
            const loadingSpinner = document.getElementById('customLoadingSpinner');
            const testimonialsCarousel = document.getElementById('testimonialsCarousel');

            let currentIndex = 0;
            let slideWidth = 100;
            let slidesPerView = 1;
            let maxIndex = 0;
            let testimonials = [];

            // Fetch testimonials from API
            async function fetchTestimonials() {
                try {
                    const response = await fetch('/api/method/mykartavya.controllers.api.user_testimonial');
                    if (!response.ok) {
                        throw new Error('Failed to fetch testimonials');
                    }

                    const data = await response.json();

                    // Filter testimonials by is_publish = 1
                    testimonials = data.message.message.filter(testimonial => testimonial.is_publish === 1);

                    // If testimonials are available, render them
                    if (testimonials && testimonials.length > 0) {
                        renderTestimonials();
                        loadingSpinner.classList.add('hidden');
                        testimonialsCarousel.classList.remove('hidden');
                        checkNavButtonsVisibility();
                    } else {
                        throw new Error('No published testimonials found');
                    }
                } catch (error) {
                    console.error('Error fetching testimonials:', error);
                    loadingSpinner.classList.add('hidden');
                    // Consider adding an error state or message here if needed
                }
            }

            // Render testimonials in the slider
            function renderTestimonials() {
                slider.innerHTML = '';

                testimonials.forEach(testimonial => {
                    const userDetails = testimonial.user_details;
                    const userType = getUserType(userDetails.user_type);
                    const userName = userDetails.full_name || 'User';
                    const designation = userDetails.designation || '';

                    const testimonialSlide = document.createElement('div');
                    testimonialSlide.className = 'testimonial-slide';
                    testimonialSlide.innerHTML = `
                        <div class="custom_testimonial">
                            <div>
                                <img src="https://res.cloudinary.com/dyt5jqnax/image/upload/v1743142172/icons8-double-quotes-48_bendmt.png" alt="Quote Icon" class="quote-icon">
                                <p class="text-gray-800 mb-6 testimonial-content">
                                    ${testimonial.comment}
                                </p>
                            </div>
                            <div>
                                <h4 class=" text-gray-600" style="font-size: 14px;">${userName} (${userType}),    <span class="text-gray-600" style="font-size: 14px;">${designation}</span></h4>

                            </div>
                        </div>
                    `;

                    slider.appendChild(testimonialSlide);
                });

                updateCarouselSettings();
            }

            // Get user type display text
            function getUserType(userType) {
                switch(userType) {
                    case 'Employee':
                        return 'Corporate';
                    case 'Individual':
                        return 'Individual';
                    case 'NGO Member':
                        return 'NGO';
                    default:
                        return userType;
                }
            }

            // Update carousel settings based on screen size
            function updateCarouselSettings() {
                const slides = document.querySelectorAll('.testimonial-slide');

                if (window.innerWidth >= 1024) {
                    slidesPerView = 3;
                    slideWidth = 100 / 3;
                } else if (window.innerWidth >= 768) {
                    slidesPerView = 2;
                    slideWidth = 50;
                } else {
                    slidesPerView = 1;
                    slideWidth = 100;
                }

                maxIndex = Math.max(0, slides.length - slidesPerView);

                // Make sure currentIndex is valid
                if (currentIndex > maxIndex) {
                    currentIndex = maxIndex;
                }

                updateSliderPosition();
                checkNavButtonsVisibility();
            }

            // Check if navigation buttons should be visible
            function checkNavButtonsVisibility() {
                // Only show navigation buttons if there are more slides than can be displayed at once
                if (testimonials.length > slidesPerView) {
                    // Show both buttons by default if there are enough testimonials
                    nextBtn.classList.remove('hidden');

                    // But hide prev button at the beginning
                    if (currentIndex === 0) {
                        prevBtn.classList.add('hidden');
                    } else {
                        prevBtn.classList.remove('hidden');
                    }

                    // Hide next button at the end
                    if (currentIndex >= maxIndex) {
                        nextBtn.classList.add('hidden');
                    } else {
                        nextBtn.classList.remove('hidden');
                    }
                } else {
                    // Hide both buttons if all testimonials fit on screen
                    prevBtn.classList.add('hidden');
                    nextBtn.classList.add('hidden');
                }
            }

            // Go to specific slide
            function goToSlide(index) {
                currentIndex = index;
                if (currentIndex < 0) currentIndex = 0;
                if (currentIndex > maxIndex) currentIndex = maxIndex;

                updateSliderPosition();
                checkNavButtonsVisibility();
            }

            // Update slider position
            function updateSliderPosition() {
                const translateX = -currentIndex * slideWidth;
                slider.style.transform = `translateX(${translateX}%)`;
            }

            // Initialize
            fetchTestimonials();

            // Event listeners
            prevBtn.addEventListener('click', () => {
                goToSlide(currentIndex - 1);
            });

            nextBtn.addEventListener('click', () => {
                goToSlide(currentIndex + 1);
            });

            window.addEventListener('resize', () => {
                updateCarouselSettings();
                checkNavButtonsVisibility();
            });

            // Auto-slide functionality
            let autoSlideInterval;

            function startAutoSlide() {
                autoSlideInterval = setInterval(() => {
                    if (currentIndex < maxIndex) {
                        goToSlide(currentIndex + 1);
                    } else {
                        goToSlide(0);
                    }
                }, 5000);
            }

            // Start auto-slide once testimonials are loaded
            function initAutoSlide() {
                if (testimonials.length > 0) {
                    startAutoSlide();

                    // Pause auto-slide on hover
                    slider.addEventListener('mouseenter', () => {
                        clearInterval(autoSlideInterval);
                    });

                    slider.addEventListener('mouseleave', () => {
                        startAutoSlide();
                    });
                }
            }

            // Start auto-slide after a short delay to ensure everything is loaded
            setTimeout(initAutoSlide, 1000);
        });
    </script>
</body>
