        .activity-badge {
            top: 15px;
            left: 15px;
            border-radius: 0px 20px 20px 0px;
        }

        .avatar-group img, .avatar-initials {
            border: 2px solid white;
            background-color: #e5e5e5;
            width: 32px;
            height: 32px;
            border-radius: 50%;
            display: flex;
            align-items: center;
            justify-content: center;
            color: white;
            font-weight: 500;
            text-transform: uppercase;
