<html lang="en">
<head>
    <meta charset="UTF-8" />
    <meta name="viewport" content="width=device-width, initial-scale=1.0" />
    <title>Activity Cards</title>
    <script src="https://cdn.tailwindcss.com"></script>
    <style>
        body {
            background-color: #f5f5f5;

        }

        .max-container {
            max-width: 1920px;
            margin: 0 auto;
        }

        .activity-carousel-container {
            position: relative;
            margin: 0 auto;
            overflow: hidden;
            padding: 0 50px;
        }

        .activity-carousel-wrapper {
            display: flex;
            transition: transform 0.5s ease;
            gap: 20px;
        }

        .activity-nav-prev,
        .activity-nav-next {
            position: absolute;
            top: 50%;
            transform: translateY(-50%);
            width: 40px;
            height: 40px;
            background-color: #f97316;
            color: white;
            border: none;
            border-radius: 50%;
            cursor: pointer;
            display: flex;
            align-items: center;
            justify-content: center;
            z-index: 10;
            transition: background-color 0.3s;
        }

        .activity-nav-prev:hover,
        .activity-nav-next:hover {
            background-color: #ea580c;
        }

        .activity-nav-prev {
            left: 0;
        }

        .activity-nav-next {
            right: 0;
        }

        .activity-card-container {
            display: flex;
            transition: transform 0.5s ease;
            gap: 20px;
        }

        .activity-card {
            flex: 0 0 auto;
            width: calc(33.333% - 20px);
            overflow: hidden;
            background: transparent;
        }

        .activity-card-image {
            height: 237px;
            object-fit: cover;
            background-color: #e5e5e5;
            width: 100%;
            border-radius: 20px;
        }

        .points-badge {
            top: 15px;
            right: 15px;
            border-radius: 20px;
        }

        .activity-badge {
            top: 15px;
            left: 15px;
            border-radius: 0px 20px 20px 0px;
        }

        .avatar-group img, .avatar-initials {
            border: 2px solid white;
            background-color: #e5e5e5;
            width: 32px;
            height: 32px;
            border-radius: 50%;
            display: flex;
            align-items: center;
            justify-content: center;
            color: white;
            font-weight: 500;
            text-transform: uppercase;
        }

        .avatar-initials {
            background-color: #f97316;
        }

        .activity-loader {
            border: 4px solid #f3f3f3;
            border-top: 4px solid #3498db;
            border-radius: 50%;
            width: 40px;
            height: 40px;
            animation: spin 2s linear infinite;
            margin: 100px auto;
        }

        @keyframes spin {
            0% { transform: rotate(0deg); }
            100% { transform: rotate(360deg); }
        }

        .activity-title {
            font-size: 16px;
            line-height: 1.4;
        }

        .activity-description {
            font-size: 12px;
            line-height: 1.5;
        }

        .date-time-info {
            font-size: 16px;
            display: flex;
            justify-content: space-between;
            align-items: center;
            width: 100%;
        }

        .hours-container {
            margin-left: auto;
            padding-left: 16px;
        }

        @media (max-width: 1024px) {
            .activity-card {
                width: calc(50% - 20px);
            }
        }

        @media (max-width: 768px) {
            .activity-card {
                width: calc(100% - 20px);
            }
            .date-time-info {
                font-size: 14px;
                flex-direction: row;
                align-items: center;
            }
        }
    </style>
</head>
<body>
    <div class="max-container">
        <div id="activity-loading" class="activity-loader"></div>
        <div class="activity-carousel-container">
            <button class="activity-nav-prev" id="activity-nav-prev">
                <svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                    <path stroke-linecap="round" stroke-linejoin="round" stroke-width="2" d="M15 19l-7-7 7-7" />
                </svg>
            </button>
            <button class="activity-nav-next" id="activity-nav-next">
                <svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                    <path stroke-linecap="round" stroke-linejoin="round" stroke-width="2" d="M9 5l7 7-7 7" />
                </svg>
            </button>
            <div id="activity-cards-container" class="activity-card-container"></div>
        </div>
        <div id="activity-error" class="hidden text-center text-lg text-red-500">Failed to load activities. Please try again later.</div>
    </div>

    <template id="activity-card-template">
        <div class="activity-card">
            <div class="relative">
                <img src="/api/placeholder/448/237" alt="Activity Image" class="activity-card-image" id="activity-image" />
                <div class="activity-badge absolute bg-orange-400 px-4 py-1 text-white" id="activity-type"></div>
                <div class="points-badge absolute flex items-center bg-white px-4 py-1 text-gray-800 shadow-md">
                    <span class="mr-1 text-orange-500">●</span>
                    <span id="activity-points"></span> Points
                </div>
            </div>
            <div class="p-4 sm:p-6">
                <h2 class="mb-2 activity-title" id="activity-title"></h2>
                <p class="mb-4 text-gray-600 activity-description" id="activity-description"></p>

                <div class="mt-4 text-gray-500 date-time-info">
                    <div class="flex items-center">
                        <svg xmlns="http://www.w3.org/2000/svg" class="mr-1 h-4 w-4" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                            <path stroke-linecap="round" stroke-linejoin="round" stroke-width="2" d="M8 7V3m8 4V3m-9 8h10M5 21h14a2 2 0 002-2V7a2 2 0 00-2-2H5a2 2 0 00-2 2v12a2 2 0 002 2z" />
                        </svg>
                        <span id="activity-date-range" class="text-xs sm:text-sm md:text-base"></span>
                    </div>

                    <div class="hours-container flex items-center">
                        <svg xmlns="http://www.w3.org/2000/svg" class="mr-1 h-4 w-4" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                            <path stroke-linecap="round" stroke-linejoin="round" stroke-width="2" d="M12 8v4l3 3m6-3a9 9 0 11-18 0 9 9 0 0118 0z" />
                        </svg>
                        <span id="activity-hours" class="text-xs sm:text-sm md:text-base"></span> hr
                    </div>
                </div>
                <div class="mt-4 flex items-center justify-between">
                    <div class="flex items-center">
                        <div class="avatar-group flex -space-x-2" id="activity-volunteers-group"></div>
                        <span class="ml-2 text-xs sm:text-sm text-gray-500" id="activity-more-volunteers"></span>
                    </div>

                    <button class="flex items-center font-medium text-orange-500 hover:text-orange-600 transition-colors duration-200">
                        <span class="text-xs sm:text-sm md:text-base">ACT NOW</span>
                        <svg xmlns="http://www.w3.org/2000/svg" class="ml-1 h-4 w-4" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                            <path stroke-linecap="round" stroke-linejoin="round" stroke-width="2" d="M14 5l7 7m0 0l-7 7m7-7H3" />
                        </svg>
                    </button>
                </div>
            </div>
        </div>
    </template>

    <script>
        const activityImageCache = new Map();
        let activityApiCallInProgress = false;
        let activityData = null;

        document.addEventListener('DOMContentLoaded', function() {
            fetchActivityData();
        });

        function getInitials(name) {
            return name
                .split(' ')
                .map(word => word[0])
                .join('')
                .slice(0, 2);
        }

        function createAvatarElement(volunteer) {
            if (volunteer.user_image) {
                const img = document.createElement('img');
                img.className = 'w-8 h-8 rounded-full';
                img.src = '/api/placeholder/32/32';
                img.alt = volunteer.full_name || 'Volunteer';
                loadActivityImage(volunteer.user_image, img);
                return img;
            } else {
                const div = document.createElement('div');
                div.className = 'avatar-initials';
                div.textContent = getInitials(volunteer.full_name || 'User');
                return div;
            }
        }

        function fetchActivityData() {
            if (activityApiCallInProgress) return;
            activityApiCallInProgress = true;

            document.getElementById('activity-loading').style.display = 'block';
            document.getElementById('activity-error').classList.add('hidden');

            if (activityData) {
                renderActivityCards(activityData);
                document.getElementById('activity-loading').style.display = 'none';
                activityApiCallInProgress = false;
                return;
            }

            fetch('/api/method/mykartavya.controllers.api.available_commitments')
                .then(response => {
                    if (!response.ok) {
                        throw new Error('Network response was not ok');
                    }
                    return response.json();
                })
                .then(data => {
                    if (data && data.message && Array.isArray(data.message)) {
                        activityData = data.message;
                        renderActivityCards(activityData);
                    } else {
                        throw new Error('Invalid data format');
                    }
                    document.getElementById('activity-loading').style.display = 'none';
                    activityApiCallInProgress = false;
                })
                .catch(error => {
                    console.error('Error fetching activities:', error);
                    document.getElementById('activity-loading').style.display = 'none';
                    document.getElementById('activity-error').classList.remove('hidden');
                    activityApiCallInProgress = false;
                });
        }

        function renderActivityCards(activities) {
            const cardsContainer = document.getElementById('activity-cards-container');
            const cardTemplate = document.getElementById('activity-card-template');

            cardsContainer.innerHTML = '';

            if (activities.length === 0) {
                cardsContainer.innerHTML = '<p class="text-center text-gray-500 text-lg">No activities available at the moment.</p>';
                return;
            }

            activities.forEach(activity => {
                const card = cardTemplate.content.cloneNode(true);

                let volunteers = [];
                try {
                    volunteers = JSON.parse(activity.volunteers || '[]');
                } catch (e) {
                    console.error('Error parsing volunteers:', e);
                }

                const activityImage = card.querySelector('#activity-image');
                if (activity.activity_image) {
                    loadActivityImage(activity.activity_image, activityImage);
                }

                card.querySelector('#activity-type').textContent = activity.activity_type || 'Activity';
                card.querySelector('#activity-points').textContent = activity.karma_points || 0;
                card.querySelector('#activity-title').textContent = activity.title || 'Untitled Activity';

                const tempDiv = document.createElement('div');
                tempDiv.innerHTML = activity.activity_description || '';
                let description = tempDiv.textContent || tempDiv.innerText || '';
                if (description.length > 100) {
                    description = description.substring(0, 100) + '...';
                }
                card.querySelector('#activity-description').textContent = description;

                try {
                    const startDate = activity.start_date ? new Date(activity.start_date) : new Date();
                    const endDate = activity.end_date ? new Date(activity.end_date) : new Date();

                    const formatDate = (date) => {
                        return `${date.getDate()} ${date.toLocaleString('default', { month: 'short' })}, ${date.getFullYear()}`;
                    };

                    card.querySelector('#activity-date-range').textContent = `${formatDate(startDate)} - ${formatDate(endDate)}`;
                } catch (e) {
                    console.error('Error formatting dates:', e);
                    card.querySelector('#activity-date-range').textContent = 'Date not available';
                }

                card.querySelector('#activity-hours').textContent = activity.hours || 0;

                const volunteersGroup = card.querySelector('#activity-volunteers-group');
                const moreVolunteers = card.querySelector('#activity-more-volunteers');

                const maxDisplayedVolunteers = 3;
                for (let i = 0; i < Math.min(volunteers.length, maxDisplayedVolunteers); i++) {
                    const volunteer = volunteers[i];
                    const avatarElement = createAvatarElement(volunteer);
                    volunteersGroup.appendChild(avatarElement);
                }

                if (volunteers.length > maxDisplayedVolunteers) {
                    moreVolunteers.textContent = `+${volunteers.length - maxDisplayedVolunteers} more`;
                }

                const actNowBtn = card.querySelector('button');
                actNowBtn.addEventListener('click', function() {
                    window.location.href = '/frontend/login';
                });

                cardsContainer.appendChild(card);
            });

            initializeActivityCarousel();
        }

        function initializeActivityCarousel() {
            const container = document.querySelector('.activity-card-container');
            const prevBtn = document.getElementById('activity-nav-prev');
            const nextBtn = document.getElementById('activity-nav-next');
            const cards = document.querySelectorAll('.activity-card');

            let currentPosition = 0;
            let cardsPerView = getCardsPerView();
            const totalCards = cards.length;

            function getCardsPerView() {
                if (window.innerWidth >= 1024) return 3;
                if (window.innerWidth >= 768) return 2;
                return 1;
            }

            function updateCarouselPosition() {
                const cardWidth = cards[0].offsetWidth + 20;
                container.style.transform = `translateX(${-currentPosition * cardWidth}px)`;
            }

            function slideNext() {
                const maxPosition = totalCards - cardsPerView;
                currentPosition = Math.min(currentPosition + 1, maxPosition);
                updateCarouselPosition();
            }

            function slidePrev() {
                currentPosition = Math.max(currentPosition - 1, 0);
                updateCarouselPosition();
            }

            nextBtn.addEventListener('click', slideNext);
            prevBtn.addEventListener('click', slidePrev);

            window.addEventListener('resize', () => {
                cardsPerView = getCardsPerView();
                currentPosition = Math.min(currentPosition, totalCards - cardsPerView);
                updateCarouselPosition();
            });
        }

        function loadActivityImage(imageSrc, imgElement) {
            if (imageSrc.startsWith('/')) {
                imageSrc = window.location.origin + imageSrc;
            }

            if (activityImageCache.has(imageSrc)) {
                if (activityImageCache.get(imageSrc) === 'error') {
                    return;
                } else {
                    imgElement.src = imageSrc;
                    return;
                }
            }

            const preloadImage = new Image();

            preloadImage.onload = function() {
                activityImageCache.set(imageSrc, 'loaded');
                imgElement.src = imageSrc;
            };

            preloadImage.onerror = function() {
                activityImageCache.set(imageSrc, 'error');
            };

            preloadImage.src = imageSrc;
        }
    </script>
</body>
