            display: flex;
            transition: transform 0.5s ease;
            gap: 20px;
        }

        .card {
            flex: 0 0 auto;
            width: calc(33.333% - 20px);
            overflow: hidden;
            background: transparent;
        }

        .card-image {
            height: 237px;
            object-fit: cover;
            background-color: #e5e5e5;
            width: 100%;
            border-radius: 20px;
        }

        .points-badge {
            top: 15px;
            right: 15px;
            border-radius: 20px;
        }

        .activity-badge {
            top: 15px;
            left: 15px;
            border-radius: 0px 20px 20px 0px;
        }

        .avatar-group img, .avatar-initials {
            border: 2px solid white;
            background-color: #e5e5e5;
            width: 32px;
            height: 32px;
            border-radius: 50%;
            display: flex;
            align-items: center;
            justify-content: center;
            color: white;
            font-weight: 500;
            text-transform: uppercase;
        }

        .avatar-initials {
            background-color: #f97316;
        }

        .loader {
            border: 4px solid #f3f3f3;
            border-top: 4px solid #3498db;
            border-radius: 50%;
            width: 40px;
            height: 40px;
            animation: spin 2s linear infinite;
            margin: 100px auto;
        }

        @keyframes spin {
            0% { transform: rotate(0deg); }
            100% { transform: rotate(360deg); }
        }

        .activity-title {
