    </style>
</head>
<body>
    <div class="container">
        <div class="info-panel">
            <!-- State List Section -->
            <div class="state-list">
                <div class="total-ngos">
                    <h3>Total NGOs</h3>
                    <p class="count">0</p>
                </div>

                <div class="search-container">
                    <input type="text" id="stateSearch" placeholder="Search state">
                </div>

                <ul id="stateList">
                    <!-- States will be dynamically populated here -->
                </ul>
            </div>

            <!-- Map Visualization Section -->
            <div id="chartdiv"></div>
        </div>
    </div>

    <script>
        $(document).ready(function() {
            fetchData();

            $('#stateSearch').on('keyup', function() {
                const query = $(this).val().toLowerCase();
                $('#stateList li').each(function() {
                    const state = $(this).text().toLowerCase();
                    $(this).toggle(state.includes(query));
                });
            });
        });

        async function fetchData() {
            try {
                const response = await fetch("/api/method/mykartavya.controllers.api.get_ngos_by_state", {
                    method: 'GET',
                    headers: {
                        'Accept': 'application/json',
                    },
                    credentials: 'include',
                    mode: 'cors'
                });
                const data = await response.json();
                const stateData = Array.isArray(data.message) ? data.message : [];

                updateStateList(stateData);
                updateTotalNGOs(stateData);
                initializeChart(stateData);

                return data;
            } catch (error) {
                console.error("Error fetching data:", error);
            }
        }

        function updateStateList(stateData) {
            const stateList = $('#stateList');
            stateList.empty();

            const sortedData = [...stateData].sort((a, b) => b.total_ngos - a.total_ngos);

            sortedData.forEach(state => {
                const listItem = `
                    <li data-state="${state.state_name}">
                        <span class="state-name">${state.state_name}</span>
                        <span class="count">${state.total_ngos}</span>
                    </li>`;
                stateList.append(listItem);
            });
        }

        function updateTotalNGOs(stateData) {
            const totalNGOs = stateData.reduce((sum, state) => sum + parseInt(state.total_ngos || 0), 0);
            $('.total-ngos .count').text(totalNGOs.toLocaleString());
        }

        function initializeChart(stateData) {
            am4core.useTheme(am4themes_animated);

            const chart = am4core.create("chartdiv", am4maps.MapChart);
            chart.geodata = am4geodata_india2019High;
            chart.projection = new am4maps.projections.Miller();

            // Disable map zooming and dragging
            chart.seriesContainer.draggable = false;
            chart.seriesContainer.resizable = false;
            chart.maxZoomLevel = 1;
            chart.minZoomLevel = 1;
            chart.mouseWheelBehavior = "none";

            // Ensure map fills container
            chart.heightPercent = 1;
            chart.widthPercent = 1;

            const polygonSeries = chart.series.push(new am4maps.MapPolygonSeries());
            polygonSeries.useGeodata = true;

            const mapData = [];
            stateData.forEach(state => {
                const stateCode = getStateCode(state.state_name);
                if (stateCode) {
                    mapData.push({
                        id: stateCode,
                        value: parseInt(state.total_ngos),
                        stateName: state.state_name
                    });
                }
            });

            polygonSeries.data = mapData;

            polygonSeries.heatRules.push({
                property: "fill",
                target: polygonSeries.mapPolygons.template,
                min: am4core.color("#e6f2e9"),
                max: am4core.color("#44aa74")
            });

            const polygonTemplate = polygonSeries.mapPolygons.template;
            polygonTemplate.tooltipText = "{stateName}\nNGOs: {value}";
            polygonTemplate.nonScalingStroke = true;
            polygonTemplate.strokeWidth = 0.5;
            polygonTemplate.stroke = am4core.color("#ffffff");

            const hs = polygonTemplate.states.create("hover");
            hs.properties.fill = am4core.color("#3c5bdc");

            polygonTemplate.events.on("hit", function(ev) {
                const data = ev.target.dataItem.dataContext;
                highlightState(data.stateName);
            });
        }

        function highlightState(stateName) {
            const stateItem = $(`#stateList li[data-state="${stateName}"]`);
            if (stateItem.length) {
                stateItem.parent().animate({
                    scrollTop: stateItem.offset().top - stateItem.parent().offset().top + stateItem.parent().scrollTop()
                });
                stateItem.css('background-color', '#fff3e0')
                    .delay(1000)
                    .queue(function(next) {
                        $(this).css('background-color', '');
                        next();
                    });
            }
        }

        function getStateCode(stateName) {
            const stateMapping = {
                'Uttarakhand': 'IN-UT',
                'Uttar Pradesh': 'IN-UP',
                'Himachal Pradesh': 'IN-HP',
                'Gujarat': 'IN-GJ',
                'Andhra Pradesh': 'IN-AP',
                'Haryana': 'IN-HR',
                'Jammu and Kashmir': 'IN-JK',
                'Jharkhand': 'IN-JH',
                'Kerala': 'IN-KL',
                'Karnataka': 'IN-KA',
                'Meghalaya': 'IN-ML',
                'Maharashtra': 'IN-MH',
                'Manipur': 'IN-MN',
                'Assam': 'IN-AS',
                'Nagaland': 'IN-NL',
                'Odisha': 'IN-OR',
                'Punjab': 'IN-PB',
                'Puducherry': 'IN-PY',
                'Tamil Nadu': 'IN-TN',
                'Telangana': 'IN-TG',
                'West Bengal': 'IN-WB',
                'Bihar': 'IN-BR',
                'Chhattisgarh': 'IN-CT',
                'Goa': 'IN-GA',
                'Madhya Pradesh': 'IN-MP',
                'Mizoram': 'IN-MZ',
                'Rajasthan': 'IN-RJ',
                'Sikkim': 'IN-SK',
                'Tripura': 'IN-TR',
                'Arunachal Pradesh': 'IN-AR',
                'Delhi': 'IN-DL',
                'Ladakh': 'IN-LA'
            };
            return stateMapping[stateName];
        }
    </script>
</body>
