script:
frappe.ui.form.on('SVA User', {
    before_workflow_action: function(frm) {
        if(frm.selected_workflow_action == 'Reject'){
            frappe.prompt(
                {
                    label: "Rejected Reason",
                    fieldname: "rejected_reason",
                    fieldtype: "Small Text",
                    reqd: 1
                },
                function(values) {
                    frm.set_value("custom_remark", values.rejected_reason);
                    frm.set_value("workflow_state", 'Rejected');
                    frm.save() 
                },
                "Please provide a reason for rejection"
            );
        }
        if (frm.doc.workflow_state == 'Pending Approval'){
            frm.set_value('custom_remark', '');
            frm.set_value('custom_remark', '');
            frm.save() 
        }
    },
    validate(frm){
        if (frm.doc.custom_phone_number){
             frm.set_value("custom_phone_number_data",frm.doc.custom_phone_number)
        }

    },
    custom_phone_number(frm) {
        const raw = frm.doc.custom_phone_number;
        if (raw) {
            // Sirf digits nikaalein
            const digits = raw.replace(/\D/g, '');
            if (digits.length > 12) {
                frappe.show_alert({
                    message: `Phone Number"${raw}" should not exceed 10 digits.`,
                    indicator: 'red'
                });
            }
        }
    },
    custom_date_of_birth: function (frm) {
        if (frm.doc.custom_date_of_birth) {
            let dob = new Date(frm.doc.custom_date_of_birth);
            let today = new Date();
            let min_date = new Date();
            min_date.setFullYear(today.getFullYear() - 18);

            if (dob > today) {
                // frappe.show_alert(__('Date of birth cannot be in the future'));
                frappe.show_alert({
                    message:  'Date of birth cannot be in the future',
                    indicator: 'red'
                });
                frm.set_value('custom_date_of_birth', '');
            } else if (dob > min_date) {
                 frappe.show_alert({
                    message: 'Date of birth must be at least 18 years ago',
                    indicator: 'red'
                });
                frm.set_value('custom_date_of_birth', '');
            }
        }
    },

    refresh(frm) {

        let today = new Date();
        let min_date = new Date();
        min_date.setFullYear(today.getFullYear() - 18);

        frm.set_df_property('custom_date_of_birth', 'max_date', frappe.datetime.str_to_obj(today));

        $(frm.fields_dict['custom_date_of_birth'].$input).datepicker({
            maxDate: today,
            yearRange: '-100:+0'
        });


    if (frm.doc.status !== 'Inactive'&& frm.doc.workflow_state !== 'Rejected'){
        frm.add_custom_button('Block', function () {
            frappe.prompt(
                {
                    label: "Block Reason",
                    fieldname: "rejected_reason",
                    fieldtype: "Small Text",
                    reqd: 1
                },
                function (values) {
                    frappe.confirm('Are you sure you want to block this user?', () => {
                        frm.set_value("custom_block_message", values.rejected_reason);
                        frm.set_value("status", "Inactive");
                        let child = frm.add_child("custom_block_log");
                        child.block_date = frappe.datetime.get_today();
                        child.block_reason = values.rejected_reason;

                        // Refresh the table UI
                        frm.refresh_field("custom_block_log");

                          frm.save().then(() => {
                             frm.reload();
                     });
                 });
                },
                "Please provide a reason for blocking this user"
            );
        });
    }

    if (frm.doc.status === 'Inactive') {
            frm.add_custom_button('Unblock', function () {
                frappe.confirm('Do you want to activate this user again?', () => {
                    frm.set_value('status', 'Active');
                      frm.save().then(() => {
                             frm.reload();
                     });
                });
            });
        }

    },

})
