if doc.custom_volunteer_type in ["Employee", "NGO Member"]:
    frappe.db.set_value("SVA User", doc.name, "workflow_state", "Approved")
if doc.email:
    email_domain = doc.email.split('@')[-1].lower()

    # Company domain match
    company = frappe.db.get_value("Company", {"company_domain": email_domain}, "name")

    if company:
        doc.db_set("custom_company", company)
        doc.custom_company = company
        doc.append("table_pdop", {
            "module": "Company",
            "value": company,
        })

if doc.custom_ngo and doc.custom_volunteer_type == "NGO Member":
    doc.append("table_pdop",{
        "module":"NGOs",
        "value":doc.custom_ngo
    })
    doc.save(ignore_permissions=True)

if doc.custom_company and doc.custom_volunteer_type == "Employee":
    doc.append(
        "table_pdop",
        {
            "module": "Company",
            "value": doc.custom_company,
        }
    )
    doc.save(ignore_permissions=True)
